const CategoryDetailScreen = () => {
  const route = useRoute();
  const { categoryName } = route.params || {};

  const products = dummyData[categoryName] || [];

  return (
    <ScrollView style={styles.container}>
      <Text style={styles.title}>{categoryName}</Text>

      <View style={styles.gridContainer}>
        {products.length > 0 ? (
          products.map((item) => (
            <View key={item.id} style={styles.card}>
              <View style={styles.productimgsection}>
                <Image source={{ uri: item.image }} style={styles.productimg} resizeMode="contain" />
              </View>

              <View style={styles.productdetailsection}>
                <Text style={styles.productname}>{item.name}</Text>

                <View style={styles.priceRow}>
                  <Text style={styles.price}>Rs. {item.price}</Text>
                  <Text style={styles.crossout}>Rs. {item.oldPrice}</Text>
                </View>

                <View style={styles.platformRow}>
                  <Text style={styles.availabletxt}>Available on:</Text>
                  <Text style={styles.platform}>{item.platform}</Text>
                </View>

                <TouchableOpacity style={styles.button}>
                  <Text style={styles.buttonText}>View Details</Text>
                </TouchableOpacity>
              </View>
            </View>
          ))
        ) : (
          <Text style={styles.noProducts}>No products found in this category.</Text>
        )}
      </View>
    </ScrollView>
  );
};

export default CategoryDetailScreen;

